		ll as = 0 , aas = 0;
		for(int i = 1 ; i <= b[1] ; i ++)
		{
			int mx[2] , rem[2] , zr = 0;rem[0] = rem[1] = 0;mx[0] = mx[1] = 1e9;ll sm[2];sm[0] = sm[1] = 0;
