			else
			{
				if(rem[1]) as += sm[0] + sm[1] - min(mx[0] , mx[1]) * 2;
				else as += sm[0] + sm[1] - 2 * mx[0];//全是负数把最小的一个扔了 
			}
		}
		aas = as;
		as = 0;
		for(int i = 1 ; i <= b[1] ; i ++) a[i] = -a[i];
