#define getline __getline
#define For(x,st,ed) for(int x=st,END=ed;x<=END;++x)
#define ForDown(x,st,ed) for(int x=st,END=ed;x>=END;--x)
int T,n;
int a[10003];
int maxv[10003];
int minv[10003];

signed main()
{
	read(T);
	while(T--)
	{
		read(n);
		minv[n+1]=0x7fffffff;
