#define MAXN 20010

int t, n;
int arr[MAXN];

bool solve (int arr[], int n) {
    bool flag1 = false, flag2 = false;
    for (int i = 2; i <= n; i++) {
        if (arr[i] < arr[i - 1]) {
            return true;
        }
    }

    return false; 
}

int main (void) {

    scanf ("%d", &t);
    while (t--) {
        scanf ("%d", &n);
        for (int i = 1; i <= n; i++) {
            scanf ("%d", arr + i);
        }

        printf (solve (arr, n) ? "YES\n" : "NO\n");
    }  

    return 0;
}
