	cin >> a >> ch;
	int len = a.size();
	for (register int i = 0;i < len; ++ i) {
		if (a[i] == ch) {
			if ((i - 0) % 2 == 0 && (len - 1 - i) % 2 == 0) {
				puts("YES");
				goto out;
			}
		}
	}
	puts("NO");
	out:;
}
