		scanf("%s%s",s+1,c+1);
		n=strlen(s+1);
		ans=false;
		for(int i=1;i<=n;++i){
			if(s[i]==c[1]&&(i&1))ans=true;
		}
		if(ans)printf("YES\n");
		else printf("NO\n");
	}
	return 0;
}
