		string s;
		cin >> s;
		int n = s.size();
		int tot = 1, flag = 0;
		for (int i = 1; i < n; i++) {
			if (s[i] != s[i-1])
				if(tot == 1) { //该子串无法被分解
					flag = 1;
					break;
				}
				else //该子串可以被分解
					tot = 1; 
			else tot++;
		}
