#include<bits/stdc++.h>
using namespace std;
#define ll long long
#define MP make_pair
const int MAXN=2005;
const int MOD=998244353;
int n,m;
char c[MAXN][MAXN]; 
int deg[MAXN<<1];bool vis[MAXN<<1];
vector<int> ve[MAXN<<1],now;
void dfs(int u){
	if(vis[u]) return;
	vis[u]=1;now.push_back(u);
	for(int v:ve[u]) dfs(v); 
}
ll solve(){
	ll ans=1;
	memset(vis,0,sizeof(vis));
	for(int i=1;i<=n+m;i++)
		if(!vis[i]){
			now.clear();
			dfs(i);
			int x=0,node=0,edg=0;
			for(int i:now) x^=deg[i],node++,edg+=ve[i].size();
			edg/=2;
			if(x==1) ans=0;
			else{
				for(int i=1;i<=edg-node+1;i++)
					ans=ans*2%MOD;
			} 
		}
	return ans;
}
