		int n=strlen(s+1);
		bool flag=0;
		_for(i,1,n) have[s[i]-'a']=1;
		_for(i,1,n) {
			++cnt[s[i]-'a'];
			int c=s[i]-'a';
			_for(j,0,'z'-'a') {
				if(!have[j]) continue;
				if(cnt[c]-cnt[j]>1||cnt[j]-cnt[c]>1)
					flag=1;
				if(cnt[c]-cnt[j]==1) {
					used[c][j]=1;
					if(used[j][c])
						flag=1;
				}
				if(cnt[j]-cnt[c]==1) {
					used[j][c]=1;
					if(used[c][j])
						flag=1;
				}
			}
		}
		puts(flag? "NO":"YES");
	}
	return 0;
}

