#include<bits/stdc++.h>
using namespace std;
// #define clear(a) memset(a,0,sizeof a)
#define int long long
const int N = 1e6+5;
int n,m,k,cnt,sum,Max,a[N],u[N],v[N],du[N],dis[N],vis[N];
vector<int> g[N];
inline int read()
{
	int x=0,f=1;char ch=getchar();
	while (ch<'0'||ch>'9'){if (ch=='-') f=-1;ch=getchar();}
	while (ch>='0'&&ch<='9'){x=x*10+ch-48;ch=getchar();}
	return x*f;
}

void init()
{
	cnt=0;
	sum=0;
	Max=-1;
	for (int i=1;i<=n;i++)
	{
		g[i].clear();
		du[i]=0;
		dis[i]=0;
		vis[i]=0;
	}
}

bool check(int x)
{
	init();
	for (int i=1;i<=n;i++)
		if(a[i]<=x)
			dis[i]=1;
	for (int i=1;i<=m;i++)
	{
		if(a[u[i]]<=x && a[v[i]]<=x)
		{
			du[v[i]]++;
			g[u[i]].push_back(v[i]);
			vis[u[i]]=vis[v[i]]=1;
		}
	}
	queue<int> q;
	for (int i=1;i<=n;i++)
	{
		if(!du[i] && vis[i])
		{
			dis[i]=1;
			q.push(i);
			sum++;
		}
		else
			sum+=vis[i];
	}
	while (!q.empty())
	{
		int u=q.front();
		q.pop();
		++cnt;
		for (int i=0;i<g[u].size();i++)
		{
			int v=g[u][i];
			du[v]--;
			dis[v]=max(dis[v],dis[u]+1);
