using namespace std;
double n,x,y;//定义double类型的，因为涉及到除法 
int ans;//记录答案 
int main(){
	cin>>n>>x>>y;//读入 
	//如果像样例2的那种情况的话，就不满足while的情况，不会进入while，直接输出答案 
	while(x/n*100<y){//用while来做 
		ans++;//新作一个木偶 
		x++;//因为x是巫婆人数，巫婆也参与游行，直接在x上加 
	}
	cout<<ans;//输出答案 
 	return 0;
}
