		else printf("YES\n");
	}
	else
	{
		if(b==0)printf("no\n");
		else printf("NO\n");
	}
}
ll T;	
ll n,m,k,p,q,r,s,a,b,c,d;
ll minn=9e18,maxx=-9e18;
int main(){
	T=read();
//	T=1;
	while(T--)
	{
		p=0;
		a=read(),b=read(),c=read(),d=read();
