char s[10];
int main(){
	int n;
	scanf("%d",&n);
	while(n--){
		scanf("%s",s);
		if((s[0]=='Y'||s[0]=='y')&&(s[1]=='E'||s[1]=='e')&&(s[2]=='S'||s[2]=='s')){
			printf("YES\n");
		}
		else{
			printf("NO\n");
		}
