	std::ios::sync_with_stdio(false);
	cin>>n;
	while(n--){
		cin>>s;
		for(int i=0;i<=2;i++){	
			if(islower(s[i]))	s[i]=toupper(s[i]);//小写字母转大写字母 
		}
		if(s=="YES")	printf("YES\n");
		else	printf("NO\n");
	}
	return 0;
}
