int t, n, m, a[maxn], cnt, cnt2, pos;

vector<int> g[maxn];

signed main() {
	cin >> t;
	while(t--) {
		cnt2 = 0;
		cnt = 0;
		int ans1 = -1, ans2 = 0;
		cin >> n >> m;
		for(int i = 0; i < n; i++) {
			g[i].clear();
			cnt = 0;
			int w = 0;
			for(int j = 0; j < m; j++) {
				int x;
				cin >> x;
				g[i].push_back(x + w);
				cnt += g[i][j] - g[0][j];
				w = g[i][j] - g[0][j];
				//cout << w << ' ';
			}
			a[i] = cnt;
			//cout << cnt << endl;
			if(i > 0) {
				if(cnt != 0) {
					cnt2++;
					ans1 = i + 1, ans2 = a[i];
				}
			}
		}
		if(cnt2 == 1) cout << ans1 << ' ' << abs(ans2) << endl;
		else cout << 1 << ' ' << abs(ans2) << endl;
	}
}
