#include <bits/stdc++.h>
#define int long long
using namespace std;

namespace IO{
	char ibuf[(1 << 20) + 1], *iS, *iT;
//	#if ONLINE_JUDGE
//	#define gh() (iS == iT ? iT = (iS = ibuf) + fread(ibuf, 1, (1 << 20) + 1, stdin), (iS == iT ? EOF : *iS++) : *iS++)
//	#else
	#define gh() getchar()
//	#endif
	#define reg register
	inline long long read () {
		reg char ch = gh();
		reg long long x = 0;
		reg char t = 0;
		while (ch < '0' || ch > '9') t |= ch == '-', ch = gh();
		while (ch >= '0' && ch <= '9') x = (x << 1) + (x << 3) + (ch ^ 48), ch = gh();
		return t ? -x : x;
	}
	inline void write(long long x) {
		if (x < 0) {
			x = ~(x - 1);
			putchar('-');
		}
		if (x > 9)
			write(x / 10);
		putchar(x % 10 + '0');
	}
}

using IO::read;
using IO::write;

const int maxn(5e5 + 500);
int T, n, SG[maxn], flg[105], S;
char s[maxn];

inline void solve () {
	n = read();
	scanf("%s", s + 1);
	int cr = 0, cb = 0;
	for (int i(1); i <= n; ++i) {
		if (s[i] == 'R') ++cr;
		else ++cb;
	}
	if (cr > cb) return puts("Alice"), void();
	if (cr < cb) return puts("Bob"), void();
	S = 0;
	int len = 0;
	for (int i = 1; i <= n; i++) {
		if (i > 1 && s[i] == s[i - 1]) S ^= SG[len], len = 0;
		++len; 
	}
	S ^= SG[len];
	puts(S ? "Alice" : "Bob");
}

signed main () {
	SG[2] = 1;
	for (int i = 3; i <= 100; i++) {
		for (int j = 0; j < i - 1; j++) flg[SG[j] ^ SG[i - j - 2]] = 1;
		while (flg[SG[i]]) ++SG[i];
		memset(flg, 0, sizeof(flg));
	} 
	for (int i = 101; i < maxn; i++) SG[i] = SG[i - 34];
	for (int i = 1; i <= 34; i++) fprintf(stderr, "%lld%c", SG[i], " \n"[i == 34]);
	T = read();
	while (T--) solve();
}
```
