void pre(){
	for(int i = 0; i <= L; i ++) b[i] = 0;
	v.clear();
}
void cl(){
	for(int i = 0; i <= L; i ++) c[i] = 0;
	v2.clear();
}
void op(){
	cl();
	int res = - 1;
	for(int i = 0; i <= L; i ++){
		if(b[i]){
			if(res + 1) c[i - res] ++;
			c[0] += b[i] - 1;
			res = i;
		}
	}
	for(auto i : v){
		if(res + 1){
			int temp = i - res;
			if(temp > L) v2.push_back(temp);
			else c[temp] ++;
			res = i;
		}else{
			res = i;
		}
	}
	sort(v2.begin(), v2.end());
	v = v2;
	for(int i = 0; i <= L; i ++){
		b[i] = c[i];
	}
}
void solve(){
