	for(int i = 1; i <= n; i ++){
		if(a[i] > L) v.push_back(a[i]);
		else b[a[i]] ++;
	}
	for(int i = 1; i < n; i ++) op();
	for(int i = 0; i < L; i ++){
		if(b[i]){
			cout << i << endl;
			return;
		}
	}
	cout << v[0] << endl;

}
