		for(RI i=1;i<=n;++i) 
		{
			cin >> a[i];
			if(!a[i])
			{
				++cnt;
				--i;--n;
			}
		}
		for(RI pos=0;n>1;pos=0)
		{
			if(cnt) 
			{
				b[++pos]=a[1];
				--cnt;
			}
			for(RI i=1;i<n;++i)
				if(a[i+1]-a[i]) b[++pos]=a[i+1]-a[i];
				else ++cnt; 
			n=pos;sort(b+1,b+n+1);
			for(RI i=1;i<=n;++i) a[i]=b[i];
		}
		printf("%d\n",a[n]);	
	} 
