};
int q;
struct Q{
	int id,l,r;
	long long ans;
}a[max_n+2];
struct TR{
	int l,r;
	long long sum;
}tr[max_n<<2];
bool cmpr(Q a,Q b){
	return a.r<b.r;
}
bool check(int i,int j,int k){
	return (!(k%i)&&!(k%j))||i+j>k;
}
void pu(int o){
	tr[o].sum=tr[o<<1].sum+tr[o<<1|1].sum;
}
void build(int o,int l,int r){
	tr[o]=(TR){l,r,0};
	if(l>=r)return;
	int mid=(l+r)>>1;
	build(o<<1,l,mid);
	build(o<<1|1,mid+1,r);
}
void upd(int o,int x,int w){
	int l=tr[o].l,r=tr[o].r;
	if(l>=r){
		tr[o].sum+=w;
		return;
	}
	int mid=(l+r)>>1;
	if(x<=mid)upd(o<<1,x,w);
	else upd(o<<1|1,x,w);
	pu(o);
}
long long query(int o,int ql,int qr){
	int l=tr[o].l,r=tr[o].r;
	if(ql<=l&&r<=qr)return tr[o].sum;
	int mid=(l+r)>>1;
	long long res=0;
	if(ql<=mid)res+=query(o<<1,ql,qr);
	if(qr>mid)res+=query(o<<1|1,ql,qr);
	return res;
}
bool cmpid(Q a,Q b){
	return a.id<b.id;
}
