	int t=0;
	while(n){
		t=t*10+n%10;
		n/=10;
	}
	return t;
}
int main(){
	cin>>n;
	primes(); //筛质数，预处理
	for(int i=2;i<=N;i++){
		int x=q(i); //反转之后的 i。
		if(x==i) continue;//注意必须是反转之后与原数不同 
		if(!f[i]&&!f[x]){
			cnt++; //第 cnt 个反素数诞生了。 
		}
		if(cnt==n){cout<<i;return 0;}
	}
	return 0;
}
```

