#include<bits/stdc++.h>
using namespace std;
bool prime(int n){
    if(n<2)return 0;
    for(int i=2;i*i<=n;++i){
        if(n%i==0)return 0;
    }
    return 1;
}
int f(int n){
    int sum=0;
    while(n){
       sum=sum*10+n%10;
       n/=10;
    }
    return sum;
}
bool F(int n){
    return f(n)!=n&&prime(n)&&prime(f(n));
}
int cnt=0;
int main(){
    int n;
    cin>>n;
    for(int i=1;;++i){
       if(F(i)){
          cnt++;
          if(cnt==n){
             printf("%d",i);
             return 0;
          }
       }
    }
} 
```

