#include<bits/stdc++.h>
using namespace std;
bool is_prime(int x)//判断素数
{
	for(int i=2;i<=sqrt(x);i++)
	{
		if(x%i==0)
		{
			return false;
		}
	}
	return true;
}
bool check(int x)//判断函数
{
	if(!is_prime(x))return false;//x必须是素数
	int n=x;//将x反转
	int s=1;//有点啰嗦大佬请见谅
	while(n)
	{
		n/=10;
		s*=10;
	}
	n=0;
	s/=10;
	int p=x; 
	while(x)
	{
		n+=(x%10)*s;
		s/=10;
		x/=10;
	}
	if(n==p)return false; //反转后的数不能与反转前的一样
	return is_prime(n);
}
int main()
{
	int a;
	cin>>a;
	int cnt=0;//计数器
	int i=13;//从第一个反素数开始
    //因为个位数反转之后和原来相同，所以不算
	while(i++)
	{
		if(check(i))//判断i是为反素数
		{
			cnt++;
			if(cnt==a)
			{
				cout<<i<<endl;
				return 0;
			}
		}
	}
	return 0;
}

