		}
		xby = 1<<gs(n);
		for(int i = 1;i <= n-n%2;i += 2){
			a[i] = xby+i/2;
		}
		if((n-n%2)%4 == 0){
			for(int i = 2;i <= n-n%2;i += 2){
				a[i] = a[i-1]+(1<<gs(a[i-1]))+(1<<(gs(a[i-1])+1));
			}
		}
		else{
			a[n-n%2] = a[n-n%2-1]+(1<<gs(a[n-n%2-1]));
			a[n-n%2-2] = a[n-n%2-3]+(1<<(gs(a[n-n%2-1])+1)); 
			for(int i = 2;i <= n-n%2-4;i += 2){
				a[i] = a[i-1]+(1<<gs(a[i-1]))+(1<<(gs(a[i-1])+1));
			}
		}
		for(int i = 1;i <= n;i++){
			if(i == n && n%2 == 1) a[i] = 0;
			printf("%d ",a[i]);
		}
		printf("\n");
