#include<bits/stdc++.h>
#define int long long
#define inf 2e18
#define mp make_pair
#define fi first
#define se second
#define pr printf
#define ps puts
#define pb push_back
#define For(i,a,b) for(i=a;i<=b;i++)
#define FOR(i,a,b) for(i=a;i>=b;i--)
using namespace std;
const int N=2e5+10;
int n,m,a[N],b[N],s[N],num[3];
int read(){
	int x=0,f=1;
	char ch=getchar();
	while(ch<'0'||ch>'9'){
		if(ch=='-') f=-1;
		ch=getchar();
	}
	while(ch>='0'&&ch<='9'){
		x=(x<<1)+(x<<3)+(ch^48);
		ch=getchar();
	}
	return x*f;
}
void solve(){
	int i,j,cnt=0;
	For(i,1,n) a[i]%=3;
	For(i,1,n) num[a[i]]++;
	if(num[0]<=n/2){
		For(i,1,n){
			if(a[i]){
				s[i]=1;
				cnt++;
			}
			if(cnt==n/2) break;
		}
		ps("0");
		For(i,1,n) pr("%lld",s[i]);
		ps("");
		return ;
	}
	else if(!num[0]){
		pr("1");
		For(i,1,n) if(i<=n/2) pr("0");else pr("1");
		ps("");
		return ;
	}
	else if(num[0]>=n/2){
		For(i,1,n){
			if(!a[i]){
				s[i]=1;
				cnt++;
			}
			if(cnt==n/2) break;
		}
		ps("2");
		For(i,1,n) pr("%lld",s[i]);
		ps("");
		return ;
	}
	ps("-1");
}
signed main(){
	int t,i,j;
	n=read();
	For(i,1,n) a[i]=read();
	solve(); 
	return 0;
}
```

