//省略快读和头文件
int n, m;
struct Edge {
	int hd[MAXN];
	int nxt[MAXN << 1], to[MAXN << 1];
	int pre[MAXN << 1];
	int col[MAXN << 1];//1R0B
	int tot = 1;

	void Add(int x, int y) {
		nxt[++tot] = hd[x];
		hd[x] = tot;
		to[tot] = y;
		pre[tot] = x;
	}
}e;

bool vis[MAXN];
vector<int> B;
int deg[MAXN];
int pa[MAXN], dpt[MAXN];
void dfs(int x, int fa) {//构建生成树
	vis[x] = true;
	pa[x] = fa;
	dpt[x] = dpt[fa] + 1;
	for(int i = e.hd[x]; i; i = e.nxt[i]) {
		int y = e.to[i];
		if(vis[y])
			continue;
		e.col[i] = e.col[i ^ 1] = 1;
		dfs(y, x);
	}
}

void Check() {
	for(int i = 2; i <= m * 2; i += 2)
		if(!e.col[i]) {
			B.push_back(e.pre[i]);
			B.push_back(e.to[i]);
			deg[e.pre[i]]++, deg[e.to[i]]++;
		}

	if(B.size() < 6)
		return ;
	for(auto it : B)//用于检查是否成环
		if(deg[it] < 2)
			return ;

	priority_queue<pair<int, int>> q;//用于找深度最大
	for(auto it : B)
		q.push(make_pair(dpt[it], it));

	int x = q.top().second;//取出这三个点中深度最深的 
	int l, r;//交换 l 和 r 的颜色 
	for(int i = e.hd[x]; i; i = e.nxt[i]) {
		int y = e.to[i];

		if(e.col[i] == 0)
			l = i;
		if(y == pa[x])
			r = i;
	}
	e.col[l] = e.col[l ^ 1] = 1;
	e.col[r] = e.col[r ^ 1] = 0;
}

int T;

int main()
{
	T = inpt();
	while(T--) {
//		memset(e.hd, 0, sizeof(e.hd));
//		memset(e.col, 0, sizeof(e.col));
//		memset(pa, 0, sizeof(pa));
//		memset(dpt, 0, sizeof(dpt));
//		e.tot = 1;

		n = inpt(), m = inpt();
		for(int i = 1; i <= m; ++i) {
			int x = inpt(), y = inpt();

			e.Add(x, y);
			e.Add(y, x);
		}

		memset(deg, 0, sizeof(deg));
		B.clear();
		memset(vis, false, sizeof(vis));
		dfs(1, 0);

		Check();

		for(int i = 2; i <= 2 * m; i += 2)
			printf("%d", e.col[i]);
		puts("");

		for(int i = 1; i <= n; ++i) {
			e.hd[i] = 0;
			pa[i] = 0;
			dpt[i] = 0;
			vis[i] = false;
			deg[i] = 0;
		}
		for(int i = 2; i <= e.tot; ++i) {
			e.col[i] = 0;
		}
		e.tot = 1;
	}


 	return 0;
}
