#include<bits/stdc++.h>
#define ll long long
#define ri register
#define all(x) (x).begin(),(x).end()
using namespace std;
const int N=2007;
int T,n,f[N][N];
string a;
inline void work(){
	cin>>a,n=a.size(),a=' '+a;
	for(int i=1;i<n;i++)f[i][i+1]=(a[i]==a[i+1]);
	for(int len=4,x,y,z;len<=n;len+=2)
		for(int l=1,r=len;r<=n;l++,r++){
			x=f[l+2][r],y=f[l+1][r-1],z=f[l][r-2];
			if((((x==1&&a[l]<a[l+1])||x==0)&&
			    ((y==1&&a[l]<a[r]  )||y==0))||
			   (((z==1&&a[r]<a[r-1])||z==0)&&
			    ((y==1&&a[r]<a[l]  )||y==0)))f[l][r]=0;
			else
			if((((x==1&&a[l]<=a[l+1])||x==0)&&
			    ((y==1&&a[l]<=a[r]  )||y==0))||
			   (((z==1&&a[r]<=a[r-1])||z==0)&&
			    ((y==1&&a[r]<=a[l]  )||y==0)))f[l][r]=1;
			else f[l][r]=2,assert(0);
		}
	if(!f[1][n])cout<<"Alice\n";
	else if(f[1][n]&1)cout<<"Draw\n";
	else cout<<"Bob\n";
}
int main(){
	ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);
	cin>>T;
	while(T--)work();
	return 0;
}
