{
	ios;
	int t;
	cin >> t;
	for (int asdf = 1; asdf <= t; asdf ++)
	{
		memset(bucket, 0, sizeof(bucket));
		int n, c, ans = 0;
		cin >> n >> c;
		for (int i = 1; i <= n; i ++)
		{
			int a;
			cin >> a;
			bucket[a] ++;
		}
		for (int i = 1; i <= 100; i ++)
		{
			if (bucket[i] > c)
