## 代码

```cpp
#include<bits/stdc++.h>
#define LL long long
#define FOR(i, x, y) for(int i = (x); i <= (y); i++)
#define ROF(i, x, y) for(int i = (x); i >= (y); i--)
using namespace std;
inline int rd()
{
	int sign = 1, re = 0; char c = getchar();
	while(!isdigit(c)) {if(c == '-') sign = -1; c = getchar();}
	while(isdigit(c)) {re = re * 10 + (c - '0'); c = getchar();}
	return sign * re;
}
int T, n;
char s1[100005], s2[100005];
int c1[30][30];
bool ans, ji;
signed main()
{
	T = rd();
	while(T--)
	{
		ans = 1; ji = 0;
		memset(c1, 0, sizeof(c1));
		n = rd();
		scanf("%s%s", s1 + 1, s2 + 1);
		for(int i = 1, j = n; i <= n; i++, j--) c1[s1[i] - 'a'][s2[j] - 'a']++;
		FOR(i, 0, 25)
		{
			if(n & 1)
			{
				if(c1[i][i] & 1)
				{
					if(ji)
					{
						ans = 0;
						break;
					}
					ji = 1;
				}
			}
			else if(c1[i][i] & 1)
			{
				ans = 0;
				break;
			}
			FOR(j, i + 1, 25)
			{
				if((c1[i][j] & 1) != (c1[j][i] & 1))
				{
					ans = 0;
					break;
				}
			}
			if(!ans) break;
		}
		if(ans) puts("YES");
		else puts("NO");
	}
	return 0;
}
```
