        {
            se.insert(a[i]);
            if(se.size()>=x*y-3)
            {
                cout<<"TIDAK\n";
                return ;
            }
        }
        else
        {
            now--;
            while(se.find(now)!=se.end())
            {
                se.erase(now);
                now--;
            }
        }
    }
    cout<<"YA\n";
