```cpp
#include<bits/stdc++.h>
#define int long long
using namespace std;
const int max_n=100005;
inline int read(){
    int x=0;bool w=0;char c=getchar();
    while(c<'0' || c>'9') w|=c=='-',c=getchar();
    while(c>='0' && c<='9') x=(x<<1)+(x<<3)+(c^48),c=getchar();
    return w?-x:x;
}
inline void write(int x){
    if(x<0) putchar('-'),x=-x;
    if(x>9) write(x/10);
    putchar(x%10^48);
}

int n;

string OK1,OK2,OK3;

vector<int> can,Ask[3];

inline void Last(){
    if(can.size()==1){
        cout<<"! "<<can[0]<<"\n";
        cout.flush();
        return;
    }
    cout<<"! "<<can[0]<<"\n";
    cout.flush();
    cin>>OK1;
    if(OK1=="YES") return;
    cout<<"! "<<can[1]<<"\n";
    cout.flush();
}

inline void push(int x,int y){
    can.clear();
    for(auto v:Ask[x]) can.emplace_back(v);
    for(auto v:Ask[y]) can.emplace_back(v);
}

signed main(){
    // freopen(".in","r",stdin);
    // freopen(".out","w",stdout);
    cin>>n;
    for(int i=1;i<=n;++i)
        can.emplace_back(i);
    while(1){
        int n=can.size();
        if(n<=2) return Last(),0;
        Ask[0].clear(),Ask[1].clear(),Ask[2].clear();
        for(int i=0;i<n;++i)
            Ask[2-i%3].emplace_back(can[i]);
        cout<<"? "<<Ask[0].size()<<" ";
        for(auto v:Ask[0]) cout<<v<<" ";cout<<"\n";
        cout.flush();
        cin>>OK1;
        if(OK1=="NO"){
            cout<<"? "<<Ask[0].size()<<" ";
            for(auto v:Ask[0]) cout<<v<<" ";cout<<"\n";
            cout.flush();
            cin>>OK2;
            if(OK2=="NO"){
                push(1,2);
                continue;
            }
        }
        cout<<"? "<<Ask[1].size()<<" ";
        for(auto v:Ask[1]) cout<<v<<" ";cout<<"\n";
        cout.flush();
        cin>>OK3;
        if(OK3=="YES") push(0,1);
        else push(0,2);
    }
    return 0;
}
