再次提醒：**在 CF 上不要用 `rand()`，请使用 `mt19937`。**~~否则你会 WA on 5（~~

代码：
```cpp
#include <algorithm>
#include <random>
#include <cstdio>
#include <ctime>

using namespace std;

typedef long long ll;

const int mod = 1e9 + 7;
mt19937 rnd(time(NULL));
int a[300007], b[600007], op[300007], pos[300007], x[300007], l[300007], r[300007], k[300007], val[600007], pre[300007];
ll tree[300007];
bool ans[300007];

inline void init(int n){
	for (register int i = 1; i <= n; i++){
		tree[i] = 0;
	}
}

inline int read(){
	int ans = 0;
	char ch = getchar();
	while (ch < '0' || ch > '9'){
		ch = getchar();
	}
	while (ch >= '0' && ch <= '9'){
		ans = ans * 10 + (ch ^ 48);
		ch = getchar();
	}
	return ans;
}

inline int lowbit(int x){
	return x & (-x);
}

inline void add(int n, int x, int k){
	while (x <= n){
		tree[x] += k;
		x += lowbit(x);
	}
}

inline ll get_sum(int x){
	ll ans = 0;
	while (x > 0){
		ans += tree[x];
		x -= lowbit(x);
	}
	return ans;
}

int main(){
	int n = read(), q = read(), m = 0;
	srand(time(NULL));
	for (register int i = 1; i <= n; i++){
		a[i] = b[++m] = read();
	}
	for (register int i = 1; i <= q; i++){
		op[i] = read();
		if (op[i] == 1){
			pos[i] = read();
			x[i] = b[++m] = read();
		} else {
			l[i] = read();
			r[i] = read();
			k[i] = read();
			ans[i] = (r[i] - l[i] + 1) % k[i] == 0;
		}
	}
	sort(b + 1, b + m + 1);
	m = unique(b + 1, b + m + 1) - b - 1;
	for (register int i = 1; i <= n; i++){
		a[i] = lower_bound(b + 1, b + m + 1, a[i]) - b;
	}
	for (register int i = 1; i <= q; i++){
		if (op[i] == 1) x[i] = lower_bound(b + 1, b + m + 1, x[i]) - b;
	}
	while (1.0 * clock() / CLOCKS_PER_SEC < 2.8){
		init(n);
		for (register int i = 1; i <= m; i++){
			val[i] = rnd() % mod;
		}
		for (register int i = 1; i <= n; i++){
			pre[i] = a[i];
			add(n, i, val[a[i]]);
		}
		for (register int i = 1; i <= q; i++){
			if (op[i] == 1){
				add(n, pos[i], val[x[i]] - val[pre[pos[i]]]);
				pre[pos[i]] = x[i];
			} else {
				ans[i] &= (get_sum(r[i]) - get_sum(l[i] - 1)) % k[i] == 0;
			}
		}
	}
	for (register int i = 1; i <= q; i++){
		if (op[i] == 2){
			if (ans[i]){
				printf("YES\n");
			} else {
				printf("NO\n");
			}
		}
	}
	return 0;
}
```
