using namespace std;
int n,m,T;
int main()
{
	cin>>T;
	while(T--)//T组数据
	{
		int n,m;
		cin>>n>>m;
		int a,b;
		for(int i=1;i<=m;i++)
		{
			cin>>a>>b;
		}
		if(m<n)
		{
			cout<<"YES"<<endl;//能
		}
		else 
		{
			cout<<"NO"<<endl;//不能
		}
	}
	return 0;
