using namespace std;
int t,n,m,x,y;
int main(){
    cin>>t;
    for(int i=1;i<=t;i++){
        cin>>n>>m;
        for(int j=1;j<=m;j++) cin>>x>>y;
        if(n==m) cout<<"NO\n";
        else cout<<"YES\n";
    }
    return 0;
