{
	T=read();
	while(T--)
	{
		n=read();
		int ma1=-1,ma2=-1;
		for(int r=1;r<=n;r++)
		{
			A[r]=read();
			B[r]=A[r];
		}
		std::sort(B+1,B+n+1);
		for(int r=1;r<=n;r++)
		{
			if(A[r]==B[n])
			{
				printf("%d ",A[r]-B[n-1]);
			}
			else
			{
				printf("%d ",A[r]-B[n]);
			}
		}
		puts("");
	}
