signed main() 
{
	int T;
	cin>>T;
	while(T--) 
	{
		int n;
		cin>>n;
		int sum=0,minn=2e9;
		for (int i=1;i<=n;i++)
		cin>>a[i],minn=min(minn,a[i]),vis[i]=0;
		vector<int>ans;
		while(minn<=2e9) 
		{
			for (int i=1;i<=n;i++)
			if(a[i]<=minn&&!vis[i])
			ans.push_back(i),ans.push_back(minn-a[i]),vis[i]=1;
			minn*=2;
		}
		cout<<ans.size()/2<<'\n';
