#include <bits/stdc++.h>
using namespace std;
#define gc c=getchar()
#define r(x) read(x)
#define ll long long
template<typename T>
inline void read(T&x)
{
    x=0;T k=1;char gc;
    while(!isdigit(c)){if(c=='-')k=-1;gc;}
    while(isdigit(c)){x=x*10+c-'0';gc;}x*=k;
}
const int maxn=1e7+20;
int prime[maxn];
bool vis[maxn];
int tot=0;
void pre(int n)
{
for(int i=2;i<n;i++)
{
    if(!vis[i])
    prime[tot++]=i;
    for(int j=0;j<tot;++j)
    {
    if(i*prime[j]>n)
    break;
    vis[i*prime[j]]=1;
    if(i%prime[j]==0)
    break;
    }
}
}
unordered_map<int,vector<int> >p;
signed main()
{
    pre(sqrt(1e7)+1);
    int T;
    for(r(T);T;--T)
    {
    	int a,b;
        r(a),r(b);
        if(b-a==1)
        	cout<<-1<<'\n';
        else if(__gcd(a,b)!=1)
        	cout<<0<<'\n';
        else
        {
        	int x=b-a;
        	vector<int>d;
        	for(int i=0;i<tot;i++)
        		if(x%prime[i]==0)
        		{
        			d.push_back(prime[i]);
        			while(x%prime[i]==0)
        				x/=prime[i];
        		}
        		else if(x<prime[i])
        			break;
        	if(x!=1)
        		d.push_back(x);
        	int ans=2e9;
        	for(auto x:d)
        		ans=min(ans,x-a%x);
        	cout<<ans<<'\n';
        }
    }
}
```

另外还有一个奇怪的做法，和上面那个其实本质一样，但时间复杂度更优：预处理求出一个数的最小质因子，这个可以朴素线性筛实现，然后对于每个数不断除其最小质因子，更新答案，递归下去，因为质因子 $ \geq 2 $，所以递归层数不会超过 $ \log{\max{a_i}} $ 层，本质上还是质因数分解，但节约了枚举无用约数的时间，时间复杂度 $ \mathcal{O}(\max{a_i}+T\log{\max{a_i}}) $，跑得比上面那个快。

代码：

```cpp
#include <bits/stdc++.h>
using namespace std;
#define gc c=getchar()
#define r(x) read(x)
#define ll long long
template<typename T>
inline void read(T&x)
{
    x=0;T k=1;char gc;
    while(!isdigit(c)){if(c=='-')k=-1;gc;}
    while(isdigit(c)){x=x*10+c-'0';gc;}x*=k;
}
const int maxn=1e7+20;
int primes[maxn],minp[maxn],cnt;
bool st[maxn];
void pre(int n)
{
	for(int i = 2 ; i <= n ; i++)
	{
		if(!st[i]) 
		minp[i] = i,primes[cnt++] = i;
		for(int j = 0 ; primes[j] * i <= n ;j++)
		{
			int t = primes[j] * i;
			st[t] = true;
			minp[t] = primes[j];
			if(i % primes[j] == 0) 
            break;
		}
	}
}
unordered_map<int,vector<int> >p;
signed main()
{
    pre(1e7);
    int T;
    for(r(T);T;--T)
    {
    	int a,b;
        r(a),r(b);
        if(b-a==1)
        cout<<-1<<'\n';
        else if(__gcd(a,b)!=1)
        cout<<0<<'\n';
        else
        {
        int x=b-a;
        int ans=2e9;
        while(x>1)
        ans=min(ans,minp[x]-a%minp[x]),x/=minp[x];
        cout<<ans<<'\n';
        }
    }
}
```

$\gcd(x+k,y+k)=1$，根据辗转相减，转化为 $\gcd(x+k,y-x)=1$，当 $y-x=1$ 时永远不互质，输出 $-1$，否则容易发现，$\gcd(x+k,y-x)\ne1$，与 $x-k$ 是 $y-x$ 的某个质因子的倍数，这两句话是等价的。而 $y-x\le10^7$，质因子不超过 $8$ 个，提前筛出来，处理询问的时候枚举每个质因子，并找到每个质因子的最小的倍数 $d$，满足 $d\ge x$，并更新答案即可。

代码：

```cpp
// Problem: D. Lucky Chains
// Contest: Codeforces - Educational Codeforces Round 139 (Rated for Div. 2)
// URL: https://codeforces.com/contest/1766/problem/D
// Memory Limit: 512 MB
// Time Limit: 4000 ms
// 
// Powered by CP Editor (https://cpeditor.org)

#include<bits/stdc++.h>
using namespace std;
namespace Wilson_Inversion{
void gi(int &x){
	x=0;
	int y=1;
	char c=getchar();
	while(c<'0'||c>'9'){
		if(c=='-')y=-1;
		c=getchar();
	}
	while(c>='0'&&c<='9'){
		x=(x<<1)+(x<<3)+(c&15);
		c=getchar();
	}
	x*=y;
}
int gcd(int x,int y){
	while(y){
		int t(x);
		x=y;
		y=t%x;
	}
	return x;
}
int n,x,y,pr[4000010],cnt,p[10000010][9],num[10000010];
bool np[10000010];
void init(){
	for(int i(2);i<=10000000;++i){
		if(!np[i])pr[++cnt]=i,num[i]=1,p[i][0]=i;
		for(int j(1);j<=cnt&&i*pr[j]<=10000000;++j){
			np[i*pr[j]]=true;
			if(!(i%pr[j])){
				for(int k(0);k<num[i];++k)p[i*pr[j]][k]=p[i][k];
				num[i*pr[j]]=num[i];
				break;
			}
			p[i*pr[j]][0]=pr[j];
			for(int k(0);k<num[i];++k)p[i*pr[j]][k+1]=p[i][k];
			num[i*pr[j]]=num[i]+1;
		}
	}
}
void solve(){
	gi(x);
	gi(y);
	if(gcd(x,y)!=1){
		puts("0");
		return;
	}
	if(abs(x-y)==1){
		puts("-1");
		return;
	}
	if(x>y)swap(x,y);
	int delt=y-x;
	x%=delt;
	int ans(delt);
	for(int i(0);i<num[delt];++i){
		ans=min(ans,((x-1)/p[delt][i]+1)*p[delt][i]-x);
	}
	printf("%d\n",ans);
}
void main(){
	gi(n);
	init();
	while(n--)solve();
}
}  // namespace Std
#undef int
int main(){
	Wilson_Inversion::main();
	return 0;
}
```
