    int _; cin>>_;
    while (_--)
    {
        string s; cin>>s;
        string s1,s2,s3;
        for (int i=1;i<s.length();i++)
            for (int j=i+1;j<s.length();j++)
            {
                s1=s.substr(0,i),s2=s.substr(i,j-i),s3=s.substr(j,s.length()-j);
                if ((s2>=s1 && s2>=s3) || (s2<=s1 && s2<=s3)) 
                {
                    cout<<s1<<" "<<s2<<" "<<s3<<endl;
                    goto bk;
                }
