#define N 1200010

int n;
int a[N];
int S,T;
bool vis[N];

int h[N * 2],e[N * 20],ne[N * 20],iidx;
void add_edge(int x,int y)
{
	ne[++iidx] = h[x];
	h[x] = iidx;
	e[iidx] = y;
}

void add(int x,int y)
{
	add_edge(x,y);
	add_edge(y,x);
}
bool flag;
int dis[N * 2];
struct node
{
	int id,dis;
	bool operator < (const node &B)const
	{
		return dis > B.dis;
	}
};

std::priority_queue <node> q;

void dijstra()
{
	memset(dis,0x3f,sizeof(dis));
	q.push((node){S,0});
	dis[S] = 0;
	while(!q.empty())
	{
		int k = q.top().id;
		q.pop();
		if(vis[k])
			continue;
		vis[k] = 1;
		for(int i = h[k];~i;i = ne[i])
		{
			int nx = e[i];
			if(dis[nx] > dis[k] + 1)
			{
				dis[nx] = dis[k] + 1;
				q.push((node){nx,dis[nx]});
			}
		}
	}
}

