#include <iostream>
#include <cstdio>
#include <vector>
using namespace std;
const int N=5e4+10,M=5e5+10;
int son[M][10],idx=1;
int n,m;
void clear(){
	for(int i=1;i<=idx;i++){
		for(int j=0;j<10;j++)
			son[i][j]=0;
	}
	idx=1;
}
void insert(int a[]){
	int u=1;
	for(int i=1;i<=n;i++){
		int x=a[i];
		if(!son[u][x])
			son[u][x]=++idx;
		u=son[u][x];
	}
}
inline int read(){
	int x=0;
	char ch=getchar();
	bool flag=false;
	while(ch<'0'||ch>'9'){
		if(ch=='-')
			flag=true;
		ch=getchar();
	}
	while(ch>='0'&&ch<='9'){
		x=(x<<1)+(x<<3)+(ch^48);
		ch=getchar();
	}
	if(flag)return -x;
	return x;
}
int a[N][12];
int pos[12];
int main(){
	int T;
	T=read();
	while(T--){
		m=read(),n=read();
		for(int i=1;i<=m;i++){
			for(int j=1;j<=n;j++)
				a[i][j]=read();
		}
		for(int p=1;p<=m;p++){
			for(int i=1;i<=n;i++)
				pos[i]=0;
			for(int i=1;i<=n;i++)
				pos[a[p][i]]=i;
			insert(pos);
		}
		for(int p=1;p<=m;p++){
			for(int i=1,u=1;i<=n;i++){
				if(!son[u][a[p][i]]){
					printf("%d ",i-1);
					break;
				}
				else
					u=son[u][a[p][i]];
				if(i==n)
					printf("%d ",n);
			}
		}
		puts("");
		clear();
	}
	return 0;
}
