  f(i,1,n) cin >> a[i];
  sort(a+1, a+n+1); r1 = r2 = 0;
  f(i,1,n) {
    if(i>1 && a[i] == a[i-1]) b[++r1] = a[i];
    else ++r2, tmp[r2] = a[r2] = a[i];
  }
  n = r2;
  f(i,1,n) g[i] = __gcd(g[i-1], a[i]), sa[i] = sa[i-1] + a[i];
  f(i,0,k) ans[i] = -inf;
  for(int l = 1, r = 1; l <= n; l = r+1) {
    r = l;
    while(r+1 <= n && g[r+1] == g[l]) ++ r;
