    d(i,l,r) {
      ans[i] = sa[n] - sa[i] + mx;
      a[i] = __gcd(a[i], g[l]);
      mx = max(mx, a[i] - tmp[i]);
    }
  }
  f(i,1,r1) sb[i] = sb[i-1] + b[i];
  II tans = -inf;
  if(k <= r1) tans = sa[n] + sb[r1] - sb[k];
  f(i,0,min(r1, k)) tans = max(tans, ans[k-i] + sb[r1] - sb[i]);
  print(tans); cout << '\n';
}
