```cpp
#include<bits/stdc++.h>
using namespace std;
int n,m;
int ans;
inline int read()
{
    int k=1;int x=0;
    char c=getchar();
    while ((c<'0'||c>'9')&&c!='-')c=getchar();
    if(c=='-')
    k=-1,c=getchar();
    while(c>='0'&&c<='9')
    x=x*10+c-'0',c=getchar();
    return k*x;
}
int main()
{
    n=read(),m=read();
    ans=m-2*n;
    ans=n-ans;
    if(ans<0)
    {
        cout<<"0";
        return 0;
    }
    else
    cout<<ans;
    return 0;
}
```

