{
	ios::sync_with_stdio(false);
	cin.tie(0);
	ll a[3];
	cin >> a[0] >> a[1] >> a[2];
	sort(a,a+3);
	if((a[1]-a[0])%2==0)
	{
		cout << a[1] << endl;
	}
	else 
	{
		cout << a[2] << endl;
	}
	return 0;
}

