```cpp
#include<bits/stdc++.h>
using namespace std;
int n,maxx,b[100010],t;
int h,m;
int a[25][60];
int main(){
    cin>>n;
    maxx=0;
    int k=1;
    for(int i=1;i<=n;i++){
        cin>>h>>m;
        a[h][m]++;
    }
    for(int i=0;i<24;i++)
        for(int j=0;j<60;j++)
            if(maxx<a[i][j])maxx=a[i][j];
	cout<<maxx<<endl;
