int main()
{
	cin>>n;//输入
	for(int i=1;i<=n;i++)
	{
		cin>>a[i];
		a[i]%=2;//对所有数%2处理
	}
	int total1=0,total0=0;//定义累加变量
	int first1,first0;//用来记录第一个1/0出现的位置
	bool flag1=false,flag0=false;
	for(int i=1;i<=n;i++)
	{
		if(a[i]==1)
		{
			total1++;//统计
			if(flag1==false)
			{
				flag1=true;
				first1=i;
			}//记录
		}
		else
		{
			total0++;//统计
			if(flag0==false)
			{
				flag0=true;
				first0=i;
			}//记录
		}
	}
	if(total1>total0) cout<<first0;//如果是1多就输出0
	else cout<<first1;//不然就输出1
	return 0;
}
