#include<bits/stdc++.h>
#define mod 1000000009
using namespace std;
int h,s,n;
vector<int>state[10000];
map<vector<int>,int>mp;
int trans[10000][4];
int dp[1010][10000];
int get_state(vector<int>v)
{
	if(mp.find(v)!=mp.end())
		return mp[v];
	state[s]=v;
	mp[v]=s;
	s++;
	return s-1;
}
void func(int id)
{
	for(int i=0;i<4;i++)
	{
		vector<int>w=state[id];
		for(int j=0;j<4;j++)
		{
			if(j==i)
				w[j]=((w[j]==h)?h:0);
			else
				w[j]=min(w[j]+1,h);
		}
		sort(w.begin(),w.end());
		trans[id][i]=get_state(w);
	}
}
int main()
{
	ios::sync_with_stdio(false);
	cin>>n>>h;
	vector<int>init;
	for(int i=0;i<4;i++)
		init.push_back(0);
	get_state(init);
	int pos=0;
	while(pos<s)
	{
		func(pos);
		pos++;
	}
	dp[0][0]=1;
	for(int i=0;i<n;i++)
	{
		for(int j=0;j<s;j++)
		{
			for(int k=0;k<4;k++)
			{
				int j2=trans[j][k];
				dp[i+1][j2]+=dp[i][j];
				if(dp[i+1][j2]>=mod)
					dp[i+1][j2]-=mod;
			}
		}
	}
	int ans=0;
	for(int i=0;i<s;i++)
	{
		bool good=false;
		for(int j=0;j<4;j++)
			if(state[i][j]!=h)
				good=true;
		if(good)
		{
			ans+=dp[n][i];
			if(ans>=mod)
				ans-=mod;
		}
	} 
	cout<<ans<<endl;
	return 0;
}

```
