#### 法二 (DFS)

```cpp
#include<bits/stdc++.h>
using namespace std;
map<string,bool> M;
string p;
bool flag;
void dfs(int t){
	for(int i=0;i<=25;i++){
		string pp=p;
		p+=char(97+i);
		if(M[p]==0){
			cout<<p; 
			flag=1;
			return;
		}
		p=pp;
	}
	for(int i=0;i<=25;i++){
		string pp=p;
		p+=char(97+i);
		dfs(t+1);
		if(flag)return;
		p=pp;
	}
	return; 
}
int main(){

	int n;
	cin>>n;
	for(int i=1;i<=n;i++){
		string s;
		cin>>s;
		for(int j=0;j<=s.length()-1;j++){
			for(int k=j;k<=s.length()-1;k++){
				string ne;
				int cnt=-1;
				for(int x=j;x<=k;x++){
					ne+=s[x];
				} 
				M[ne]=1;
			}
		}
	}
	dfs(0);
	return 0;
} 
