#include <bits/stdc++.h>
using namespace std;
int n, m;
int flag[20005]; 
int size[10005]; // 并查集内节点个数 
int fa[10005]; // fa[x] = y 表示 y 是 x 的 father（把 x 接到 y 上） 
int Find_Set (int x) {
	if (fa[x] == x) return x;
	else return fa[x] = Find_Set (fa[x]);
}
void Union_Set (int x, int y) {
	int a = Find_Set (x);
	int b = Find_Set (y);
	if (a != b) {
		fa[a] = b;
		size[b] += size[a];
	}
}
